public String convert(String s, int nRows) {
        int n = s.length();
        if (n <= 1 || nRows <= 1) return s;
        StringBuffer res = new StringBuffer();
        for (int i = 0; i < nRows; ++i) {
            for (int j = 0; j + i < n; j += 2*nRows - 2) {
                res.append(s.charAt(j+i));
                if (i == 0 || i == nRows - 1) continue;
                if (j + 2*nRows - 2 - i < n) 
                    res.append(s.charAt(j + 2*nRows - 2 - i));
            }
        }
        return res.toString();
    }

public int trap_1(int[] A) {
        int n = A.length;
        if (n == 0) return 0;
        int[] maxLeft = new int[n];
        int[] maxRight = new int[n];
        maxLeft[0] = A[0];
        maxRight[n - 1] = A[n - 1];
        for (int i = 1; i < n; ++i) {
            maxLeft[i] = Math.max(maxLeft[i - 1], A[i]);
            maxRight[n - 1 - i] = Math.max(maxRight[n - i], A[n - 1 - i]);
        }
        int res = 0;
        for (int i = 1; i < n; ++i) {
            res += Math.min(maxLeft[i], maxRight[i]) - A[i];
        }
        return res;
    }

