                int key=o.getFreq();
                while (true){
                    if (n.getElement().getFreq()>key){
                        DLNode second=n.getPrev();                      
                        DLNode huf=new DLNode(n,second,o);
                        second.setNext(huf);
                        n.setPrev(huf);
                        break;
                    }
                    if (n.getNext()==tail){
                        DLNode huf=new DLNode(tail,n,o);
                        n.setNext(huf);
                        tail.setPrev(huf);
                        break;
                    }
                    n=n.getNext();
                }
            }
        size++;
    }

