public static String buildVideoCommand(
            String  vcodec,
            String  acodec,
            int     bitrate_v,
            int     bitrate_a,
            int     sampling,
            String  videoSrc,
            String  outputFile) throws InvalidMediaParameterException {
if (bitrate_v < 1) {
            throw new InvalidMediaParameterException("Invalid video bitrate.[" + bitrate_v + "]");
