public boolean isPalindrome_1(int x) {
        if (x < 0) return false;
        int d = 1;
        while (x / d >= 10) d *= 10;
        while (d > 1) {
            if (x % 10 != x / d) return false;
            x = (x % d) / 10;
            d /= 100;
        }
        return true;
    }

