public void totalNQueensRe2(int n, int row, int ld, int rd, int[] res) {
        if (row == (1<<n) -1 ) {
            res[0]++;
            return;
        }
        int avail = ~(row | ld | rd);
        for (int i = n - 1; i >= 0; --i) {
            int pos = 1<<i;
            if ((int)(avail&pos) != 0) {
                totalNQueensRe2(n, row | pos, (ld|pos) << 1, (rd|pos) >>1, res);
            }
        }
    }

