public String longestPalindrome_4(String s) {
        int n = s.length();
        int idx = 0, maxLen = 0;
        StringBuffer sb = new StringBuffer();
        sb.append('^');
        for (int i = 0; i < n; ++i) {
            sb.append('#');
            sb.append(s.charAt(i));
        }
        sb.append("#$");
        n = 2 * n + 3;
        int mx = 0, id = 0;
        int[] p = new int[n];
        Arrays.fill(p,0);
        for (int i = 1; i < n - 1; ++i) {
            p[i] = (mx > i) ? Math.min(p[2 * id - i], mx - i) : 0;
            while (sb.charAt(i + 1 + p[i]) == sb.charAt(i - 1 - p[i])) ++p[i];
            if (i + p[i] > mx) {
                id = i; mx = i + p[i];
            }
            if (p[i] > maxLen) {
                idx = i; maxLen = p[i];
            }
        }
        idx = (idx - maxLen - 1) / 2;
        return s.substring(idx, idx + maxLen);
    }

