            return null;
        }
        if(defaultReply.getContent() == null || defaultReply.getContent().trim().isEmpty()){
            logger.info("公众号["+ accountId+"," +defaultReply.getAccount().getName()+ "]设置的默认回复信息为空！");
            return null;
